import React, { Component } from 'react';
import { DataTable } from 'carbon-components-react';

const url = "http://localhost:3000/data";

const headers = [
    {
        key: "foo",
        header: "Foo"
    },
    {
        key: "bar",
        header: "Bar"
    },
    {
        key: "baz",
        header: "Baz"
    }
];
const {
    Table,
    TableHead,
    TableHeader,
    TableBody,
    TableCell,
    TableContainer,
    TableRow,
    TableSelectAll,
    TableSelectRow
} = DataTable;

class SimpleDataTable extends Component {
    constructor(props){
        super(props);
        this.state = {
            people : []
        };
    }

    componentDidMount() {
        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                this.setState({ people: data });
            })
    }

    render() {
        return (
            <div>
                <DataTable
                    rows={this.state.people}
                    headers={headers}
                    render={({
                        rows,
                        headers,
                        getHeaderProps,
                        getSelectionProps,
                        selectedRows
                    }) => (
                            <TableContainer>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableSelectAll {...getSelectionProps()} />
                                            {headers.map(header => (
                                                <TableHeader {...getHeaderProps({ header })}>
                                                    {header.header}
                                                </TableHeader>
                                            ))}
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {rows.map(row => (
                                            <TableRow key={row.id}>
                                                <TableSelectRow {...getSelectionProps({ row })} />
                                                {row.cells.map(cell => (
                                                    <TableCell key={cell.id}>{cell.value}</TableCell>
                                                ))}
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>)} />
            </div>
        );
    }
}

export default SimpleDataTable;
