import React, { Component } from 'react';
import { DataTable } from 'carbon-components-react';

const url = "http://localhost:3000/data";

const headers = [
    {
        key: "name",
        header: "Name"
    },
    {
        key: "protocol",
        header: "Protocol"
    },
    {
        key: "port",
        header: "Port"
    },
    {
        key: "rule",
        header: "Rule"
    }
];
const {
    Table,
    TableHead,
    TableHeader,
    TableBody,
    TableCell,
    TableContainer,
    TableRow,
    TableSelectAll,
    TableSelectRow,
    TableToolbar,
    TableToolbarSearch
} = DataTable;

class SimpleDataTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            people: []
        };
    }

    componentDidMount() {
        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                this.setState({ people: data });
            })
    }

    onSearch = e => {
        console.log(e.target.value);
    };

    render() {
        return (
            <div>
                <DataTable
                    rows={this.state.people}
                    headers={headers}
                    render={({
                        rows,
                        headers,
                        getHeaderProps,
                        getSelectionProps,
                        selectedRows,
                        onInputChange
                    }) => (
                            <TableContainer title="Test Table Component">
                                <TableToolbar>
                                    {/* pass in `onInputChange` change here to make filtering work */}
                                    <TableToolbarSearch onChange={onInputChange} />
                                </TableToolbar>
                                <TableToolbarContent>
                                    <TableToolbarAction
                                        icon={iconDownload}
                                        iconDescription="Download"
                                        onClick={action('TableToolbarAction - Download')}
                                    />
                                    <TableToolbarAction
                                        icon={iconEdit}
                                        iconDescription="Edit"
                                        onClick={action('TableToolbarAction - Edit')}
                                    />
                                    <TableToolbarAction
                                        icon={iconSettings}
                                        iconDescription="Settings"
                                        onClick={action('TableToolbarAction - Settings')}
                                    />
                                    <Button onClick={action('Add new row')} small kind="primary">
                                        Add new
          </Button>
                                </TableToolbarContent>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableSelectAll {...getSelectionProps()} />
                                            {headers.map(header => (
                                                <TableHeader {...getHeaderProps({ header })}>
                                                    {header.header}
                                                </TableHeader>
                                            ))}
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {rows.map(row => (
                                            <TableRow key={row.id}>
                                                <TableSelectRow {...getSelectionProps({ row })} />
                                                {row.cells.map(cell => (
                                                    <TableCell key={cell.id}>{cell.value}</TableCell>
                                                ))}
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>)} />
            </div>
        );
    }
}

export default SimpleDataTable;
